/**
 * Unescape special characters in the given string of html.
 *
 * @param  {String} html
 * @return {String}
 */
module.exports = function(html) {
    return String(html)
      .replace(/&amp;/g, '&')
      .replace(/&quot;/g, '"')
      .replace(/&#39;/g, "'")
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>');
};
